

class Blueprint {

constructor(options,driver) {
this.rows = new Map(options.rows);
this.key = options.key;
this.database = options.database;
this.run = driver.run;
this.driver = d => driver;
this.extends = options.extends;
}

extend(funcs) { Object.assign(this, funcs);}

implement(blueprint) {
(blueprint.extends || []).forEach((v,i) => {
this.extends.push(v);
this[v] = this[v] ? this[v] : blueprint[v];
})
}

}

module.exports = Blueprint;