

const Stack = require("./stack");


class Chain extends Stack {

constructor(options) {
super(options);
let maps  = options.driver().maps;
let extended_funcs = {};
(options.blueprint.extends || [])
.forEach((func,i) => {
if(!this[func]) extended_funcs[func] = options.blueprint[func];
});
Object.assign(this, extended_funcs);
}


free(p) {
this.connection.release();
return p;
}


}

module.exports = Chain;