

class Vorm {


constructor(orm) {
Object.assign(this, require(`./${orm}.js`)());
this.maps = [];
}

/* source(src) {
this.server = src;
} */

define(map, options) {
this.maps.push(map)
return Object.assign(this[map] ? {} : this, { [map]: this.blueprint(options)})
}


}

module.exports = orm => new Vorm(orm);